(8, 1, '2023-09-30 22:29:07', '2023-09-30 22:29:07', '<!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":44,\"width\":\"428px\",\"height\":\"286px\",\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large is-resized\"><img src=\"http://hpekrul.bitlampsites.com/wpd/final/wordpress/wp-content/uploads/2023/10/author-1024x683.jpg\" alt=\"Dr seuss the author\" class=\"wp-image-44\" style=\"width:428px;height:286px\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:paragraph -->\n<p></p>\n<!-- /wp:paragraph -->\n\n<!-- wp:paragraph -->\n<p>Theodor Seuss Geisel was born March 2nd, 1904. He passed away September 27, 1991. Dr. Seuss was an American writer and illustrator for children\'s books. His books are popular for their nonsense words, playful rhymes and odd creatures. </p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group --></div>\n<!-- /wp:group -->\n\n<!-- wp:separator -->\n<hr class=\"wp-block-separator has-alpha-channel-opacity\"/>\n<!-- /wp:separator -->\n\n<!-- wp:shortcode -->\n[random-review]\n<!-- /wp:shortcode -->', 'Author Bio', '', 'publish', 'closed', 'closed', '', 'author-bio', '', '', '2024-01-28 01:18:55', '2024-01-28 01:18:55', '', 0, 'http://hpekrul.bitlampsites.com/wpd/final/wordpress/?page_id=8', 0, 'page', '', 0),
